'use strict';




jest.dontMock('../getImageSource');

var getImageSource=require('../getImageSource');

describe('getImageSource',function(){
it('returns null for invalid input',function(){
expect(getImageSource().uri).toBe(null);});


it('returns a movie thumbnail',function(){
var uri='https://facebook.com';
var source={
posters:{
thumbnail:uri}};


expect(getImageSource(source).uri).toBe(uri);});


it('returns a movie thumbnail with kind',function(){
var uri='https://facebook.com?tmb';
var source={
posters:{
thumbnail:uri}};


expect(getImageSource(source,'kind').uri).toBe(
'https://facebook.com?kind');});});